    def VentanaControl(self):
        self.ventana = QtWidgets.QMainWindow()
        self.cru = InControl.Ui_MainWindow()
        self.cru.setupUi(self.ventana)
        self.cru.varid=self.var
        id=self.var
        id=str("'"+id+"'")
        dato=self.datosTotal.Filtroid(id)
        nom=dato[0][1]
        apellido=dato[0][2]
        identi=dato[0][4]
        fech=dato[0][5]
        self.cru.HisNombre.setText(str(nom))
        self.cru.HisApellido.setText(str(apellido))
        self.cru.HisIdenti.setText(str(identi))
        self.cru.HisFechaNac.setText(str(fech))

        self.ventana.show()
        MainWindow.hide()
